import React, { Component } from 'react';

class Cadastro extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: '',
      age: '',
      email: '',
      isButtonDisabled: true,
    }
  }

  inputHandler = ({ target }) => {
    const { email } = this.state;
    const { name, value } = target;
    this.setState({ [name]: value }, this.validateEmail)
    email === '' && <p>Insira um e-mail válido</p>
  }

  validateEmail = () => {
    const emailREGEX = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g
    const { email } = this.state;

    if (emailREGEX.test(email)) {
      this.setState({ isButtonDisabled: false });
    } else {
      this.setState({ isButtonDisabled: true });
    }
  }

  submitHandler = (event) => {
    const { email } = this.state;
    event.preventDefault();

    email === '' && <p>Insira um e-mail válido</p>

  }

  render() {
    const { isButtonDisabled, email } = this.state;

    return (
      <div>
        <form onSubmit={ this.submitHandler }>
          <label htmlFor="name">
            Nome:
            <input type="text" name="name" id="name" onChange={ this.inputHandler }/>
          </label>

          <label htmlFor="age">
            Idade:
            <input type="number" name="age" id="age" onChange={ this.inputHandler }/>
          </label>

          <label htmlFor="email">
            Email:
            <input type="email" name="email" id="email" onChange={ this.inputHandler }/>
          </label>

          {
            // email === '' && <p>Insira um e-mail válido</p>
          }

          <button type="submit" disabled={ isButtonDisabled }>Salvar</button>
        </form>
      </div>
    );
  }
}

export default Cadastro;
